import { useState, useEffect } from "react";
import axios from "axios";

import Button from "@mui/material/Button";
import TextField from "@mui/material/TextField";
import Dialog from "@mui/material/Dialog";
import DialogActions from "@mui/material/DialogActions";
import DialogContent from "@mui/material/DialogContent";

const EditForm = (props) => {
  const [editReview, setEditReview] = useState({});
  let [singReview, setSingReview] = useState([]);

  const handleSubmit = async () => {
    let post = await axios.post(
      `http://localhost:3001/review/${props.review.brewId}`,
      {
        brewId: props.brewery.id,
        user: editReview.user,
        title: editReview.title,
        body: editReview.body,
      }
    );
    // console.log(post);
  };

  const handleChange = (e) => {
    let entry = e.target.value;
    console.log(props.review);
    switch (e.target.id) {
      case "title":
        setEditReview({
          ...editReview,

          title: entry,
        });
        break;

      case "body":
        setEditReview({
          ...editReview,

          body: entry,
        });
        break;

      case "user":
        setEditReview({
          ...editReview,
          user: entry,
        });
        break;

      default:
        console.log("Review error");
        break;
    }
    // console.log(newReview);
  };

  useEffect(() => {
    const getReview = async () => {
      let result = await axios.get(
        `http://localhost:3001/review/single/${props.details}`
      );
      setSingReview(result);
    };
    getReview();
  });

  return singReview.length >= 1 ? (
    <Dialog open={props.open} onClose={props.handleClose}>
      <DialogContent>
        <TextField
          autoFocus
          margin="dense"
          id="title"
          label="Title"
          InputLabelProps={{ shrink: true }}
          type="text"
          fullWidth
          variant="standard"
          onChange={handleChange}
          required={true}
          placeholder={`${props.review.title}`}
        />
        <TextField
          margin="dense"
          id="user"
          label="Name"
          InputLabelProps={{ shrink: true }}
          type="text"
          fullWidth
          variant="standard"
          onChange={handleChange}
          required={true}
          placeholder={`${props.review.user}`}
        />
        <TextField
          margin="dense"
          id="body"
          label="Review"
          InputLabelProps={{ shrink: true }}
          type="text"
          fullWidth
          variant="standard"
          onChange={handleChange}
          required={true}
          placeholder={`${props.review.body}`}
        />
      </DialogContent>
      <DialogActions>
        <Button onClick={props.handleClose}>Cancel</Button>
        <Button onSubmit={handleSubmit}>Submit</Button>
      </DialogActions>
    </Dialog>
  ) : (
    <div>Loading</div>
  );
};

export default EditForm;
