import axios from "axios";
import { useState, useEffect } from "react";
import Button from "@mui/material/Button";
import EditForm from "./EditForm";
import ReviewForm from "./ReviewForm";
import Alert from "./Alert";
import TextField from "@mui/material/TextField";
import Dialog from "@mui/material/Dialog";
import DialogActions from "@mui/material/DialogActions";
import DialogContent from "@mui/material/DialogContent";
import "../styles/Review.css";

const Reviews = ({ brewery, reviews, toggle, setToggle }) => {
  const [open, setOpen] = useState(false);
  const [edit, setEdit] = useState({});
  const [editReview, setEditReview] = useState({});
  let [details, setDetails] = useState();
  const [deleteToggle, setDeleteToggle] = useState(false);

  useEffect(() => {}, [toggle]);

  const handleDelete = async (id) => {
    let dlte = await axios.delete(`http://localhost:3001/review/${id}`);
    setToggle(!toggle);
    setToggle(!toggle);
  };

  const deleteClose = () => {
    setDeleteToggle(false);
  };

  const handleToggle = () => {
    setToggle(true);
  };

  const handleOpen = async (e) => {
    setOpen(true);
  };

  const handleClose = () => {
    setDetails({});
    setOpen(false);
  };

  const handleChange = (e) => {
    let entry = e.target.value;
    // console.log(review);
    switch (e.target.id) {
      case "title":
        setEditReview({
          ...editReview,

          title: entry,
        });
        break;

      case "body":
        setEditReview({
          ...editReview,

          body: entry,
        });
        break;

      case "user":
        setEditReview({
          ...editReview,
          user: entry,
        });
        break;

      default:
        console.log("Review error");
        break;
    }
  };

  const handleSubmit = async () => {
    let post = await axios.post(
      `http://localhost:3001/review/${review.brewId}`,
      {
        brewId: brewery.id,
        user: editReview.user,
        title: editReview.title,
        body: editReview.body,
      }
    );
    // console.log(post);
  };
  return toggle ? (
    <div className={`reviews-container`}>
      <ReviewForm brewery={brewery} />
      {reviews.map((review) => (
        <div key={review._id}>
          <h2>{review.title}</h2>
          <h3>{review.user}</h3>
          <p>{review.body}</p>
          <button name={`${review._id}`} onClick={handleOpen}>
            Edit
          </button>
          <button name={`${review._id}`}>Delete</button>
          <Dialog open={open} onClose={handleClose}>
            <DialogContent>
              <TextField
                autoFocus
                margin="dense"
                id="title"
                label="Title"
                InputLabelProps={{ shrink: true }}
                type="text"
                fullWidth
                variant="standard"
                onChange={handleChange}
                required={true}
                placeholder={`${review.title}`}
              />
              <TextField
                margin="dense"
                id="user"
                label="Name"
                InputLabelProps={{ shrink: true }}
                type="text"
                fullWidth
                variant="standard"
                onChange={handleChange}
                required={true}
                placeholder={`${review.user}`}
              />
              <TextField
                margin="dense"
                id="body"
                label="Review"
                InputLabelProps={{ shrink: true }}
                type="text"
                fullWidth
                variant="standard"
                onChange={handleChange}
                required={true}
                placeholder={`${review.body}`}
              />
            </DialogContent>
            <DialogActions>
              <Button onClick={handleClose}>Cancel</Button>
              <Button onSubmit={handleSubmit}>Submit</Button>
            </DialogActions>
          </Dialog>
          <Alert
            open={deleteToggle}
            handleClose={deleteClose}
            handleDelete={handleDelete}
            review={review}
          />
        </div>
      ))}
    </div>
  ) : (
    <div>
      <Button variant="standard" onClick={handleToggle}>
        Open Reviews
      </Button>
    </div>
  );
};

export default Reviews;
