import axios from "axios";
import { useState, useEffect } from "react";
import Button from "@mui/material/Button";
import TextField from "@mui/material/TextField";
import Dialog from "@mui/material/Dialog";
import DialogActions from "@mui/material/DialogActions";
import DialogContent from "@mui/material/DialogContent";
import DialogContentText from "@mui/material/DialogContentText";
import DialogTitle from "@mui/material/DialogTitle";

import ReviewForm from "./ReviewForm";

const Reviews = ({ brewery }) => {
  const [toggle, setToggle] = useState(false);
  let [reviews, setReviews] = useState([]);

  useEffect(() => {
    const getReviews = async () => {
      let reviewList = await axios.get(
        `http://localhost:3001/review/${brewery.id}`
      );
      setReviews(reviewList.data);
    };
    getReviews();
  }, [reviews]);

  const handleDelete = (e) => {};

  const handleEdit = (e) => {
    setToggle(true);
  };

  const handleClose = () => {
    setToggle(false);
  };

  return reviews.length >= 1 ? (
    <div className={`main-bg-color`}>
      <ReviewForm brewery={brewery} />
      {reviews.map((review) => (
        <div>
          <h2>{review.title}</h2>
          <h3>{review.user}</h3>
          <p>{review.body}</p>
          <p>{review.rating}/5</p>
          <button name={`${review._id}`}>Edit</button>
          <button name={`${review._id}`}>Delete</button>
          <Dialog open={toggle}>
            <DialogContent>
              <TextField
                autoFocus
                margin="dense"
                id="title"
                label="Title"
                type="text"
                fullWidth
                variant="standard"
                value={`${title}`}
              />
            </DialogContent>
          </Dialog>
        </div>
      ))}
    </div>
  ) : (
    <div>Loading</div>
  );
};

export default Reviews;
