import axios from "axios";
import { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import GoogleMapReact from "google-map-react";
import "../styles/Details.css";

const Details = () => {
  let [brewery, setBrewery] = useState([]);
  let { name, lati, long } = useParams();
  let address = "Not Available";

  const defaultProps = {
    center: {
      lat: lati,
      lng: long,
    },
    zoom: 11,
  };

  useEffect(() => {
    const getBrewery = async () => {
      let select = await axios.get(
        `https://api.openbrewerydb.org/breweries?by_name=${name}`
      );
      setBrewery(select.data[0]);
    };
    getBrewery();
    if (brewery.street != null) {
      address =
        brewery.street +
        " " +
        brewery.city +
        ", " +
        brewery.state +
        " " +
        brewery.postal_code;
    }
  }, []);

  // console.log(lati);

  return lati === "null" ? (
    <div className={`main-bg-color details-container`}>
      <h1>{brewery.name}</h1>
      <h3>{address}</h3>
      <h3>{brewery.phone}</h3>
      <h3>{brewery.website_url}</h3>
    </div>
  ) : (
    <div className={`main-bg-color details-container`}>
      <div>
        <h1>{brewery.name}</h1>
        <h3>{address}</h3>
        <h3>{brewery.phone}</h3>
        <h3>{brewery.website_url}</h3>
        <div className={`map`}></div>
      </div>
    </div>
  );
};

export default Details;
