import axios from "axios";
import { useState } from "react";

const ReviewForm = (props) => {
  const [newReview, setNewReview] = useState({});

  const handleSubmit = async () => {
    let post = newReview;
    // let post = await axios.post(`http://localhost:3001/review`, {
    //   brewId: props.name,
    //   user: newReview.user,
    //   review: {
    //     title: newReview.review.title,
    //     body: newReview.review.body,
    //     rating: newReview.review.rating,
    //   },
    // });
    console.log(post);
    props.setToggle(!props.toggle);
  };

  const handleChange = (e) => {
    let entry = e.target.value;

    switch (e.target.id) {
      case "title":
        setNewReview({
          ...newReview,
          review: {
            title: entry,
          },
        });
        break;

      case "body":
        setNewReview({
          ...newReview,
          review: {
            body: entry,
          },
        });
        break;

      case "user":
        setNewReview({
          ...newReview,
          user: entry,
        });
        break;

      case "rating":
        setNewReview({
          ...newReview,
          review: {
            rating: parseInt(entry),
          },
        });
        break;

      default:
        console.log("Review error");
        break;
    }
    console.log(newReview);
  };

  console.log(props.name);

  return (
    <div>
      <form onSubmit={handleSubmit}>
        <label>Leave a Review</label>
        <input
          type="text"
          id="title"
          placeholder="Title"
          onChange={handleChange}
        ></input>
        <textarea
          placeholder="Review"
          id="body"
          onChange={handleChange}
        ></textarea>
        <input
          type="text"
          id="user"
          placeholder="Name"
          onChange={handleChange}
        ></input>
        <input
          type="text"
          id="rating"
          placeholder="Rating"
          onChange={handleChange}
        ></input>
        /5
        <input type="submit"></input>
      </form>
    </div>
  );
};

export default ReviewForm;
