import axios from "axios";
import { useState, useEffect } from "react";
import Button from "@mui/material/Button";
import EditForm from "./EditForm";
import ReviewForm from "./ReviewForm";
import Alert from "./Alert";
import "../styles/Review.css";

const Reviews = ({ brewery, reviews }) => {
  const [toggle, setToggle] = useState(false);
  const [open, setOpen] = useState(false);
  const [edit, setEdit] = useState({});
  let [details, setDetails] = useState();
  const [deleteToggle, setDeleteToggle] = useState(false);

  useEffect(() => {}, [toggle]);

  const handleDelete = async (id) => {
    let dlte = await axios.delete(`http://localhost:3001/review/${id}`);
    setToggle(!toggle);
    setToggle(!toggle);
  };

  const deleteClose = () => {
    setDeleteToggle(false);
  };

  const handleToggle = () => {
    setToggle(true);
  };

  const handleOpen = async (e) => {
    setDetails(e.target.name);
    console.log(details);
    setOpen(true);
  };

  const handleChange = (e) => {};

  const handleClose = () => {
    setDetails({});
    setOpen(false);
  };

  return toggle ? (
    <div className={`reviews-container`}>
      <ReviewForm brewery={brewery} />
      {reviews.map((review) => (
        <div key={review._id}>
          <h2>{review.title}</h2>
          <h3>{review.user}</h3>
          <p>{review.body}</p>
          <button name={`${review._id}`} onClick={handleOpen}>
            Edit
          </button>
          <button name={`${review._id}`}>Delete</button>
          <EditForm
            details={details}
            open={open}
            handleClose={handleClose}
            brewery={brewery}
            toggle={toggle}
          />
          <Alert
            open={deleteToggle}
            handleClose={deleteClose}
            handleDelete={handleDelete}
            review={review}
          />
        </div>
      ))}
    </div>
  ) : (
    <div>
      <Button variant="standard" onClick={handleToggle}>
        Open Reviews
      </Button>
    </div>
  );
};

export default Reviews;
