import axios from "axios";
import { useState, useEffect } from "react";
import Button from "@mui/material/Button";
import EditForm from "./EditForm";
import ReviewForm from "./ReviewForm";
import Alert from "./Alert";

const Reviews = ({ brewery }) => {
  const [toggle, setToggle] = useState(false);
  const [open, setOpen] = useState(false);
  let [reviews, setReviews] = useState([]);
  const [edit, setEdit] = useState({});
  const [deleteToggle, setDeleteToggle] = useState(false);

  useEffect(() => {
    const getReviews = async () => {
      let reviewList = await axios.get(
        `http://localhost:3001/review/${brewery.id}`
      );
      setReviews(reviewList.data);
    };
    getReviews();
  }, [toggle]);

  const handleDelete = async (id) => {
    let dlte = await axios.delete(`/review/${id}`);
    setToggle(!toggle);
    setToggle(!toggle);
  };

  const deleteClose = () => {
    setDeleteToggle(false);
  };

  const handleToggle = () => {
    setToggle(true);
  };

  const handleOpen = () => {
    setOpen(true);
  };

  const handleChange = (e) => {};

  const handleClose = () => {
    setOpen(false);
  };

  return toggle ? (
    <div className={`main-bg-color`}>
      <ReviewForm brewery={brewery} />
      {reviews.map((review) => (
        <div>
          <h2>{review.title}</h2>
          <h3>{review.user}</h3>
          <p>{review.body}</p>
          <button name={`${review._id}`} onClick={handleOpen}>
            Edit
          </button>
          <button name={`${review._id}`}>Delete</button>
          <EditForm review={review} open={open} handleClose={handleClose} />
          <Alert
            open={deleteToggle}
            handleClose={deleteClose}
            handleDelete={handleDelete}
            review={review}
          />
        </div>
      ))}
    </div>
  ) : (
    <div>
      <Button variant="standard" onClick={handleToggle}>
        Open Reviews
      </Button>
    </div>
  );
};

export default Reviews;
