import axios from "axios";
import { useState } from "react";

const ReviewForm = (props) => {
  const [newReview, setNewReview] = useState({});

  const handleSubmit = async () => {
    let post = await axios.post(`http://localhost:3001/review/:${props.name}`, {
      brewId: props.name,
      user: newReview.user,
      review: {
        title: newReview.review.title,
        body: newReview.review.body,
        rating: newReview.review.rating,
      },
    });
    console.log(post);
    props.setToggle(!props.toggle);
  };

  const handleChange = (e) => {
    let entry = e.target.value;

    switch (e.target.id) {
      case "title":
        setNewReview({
          ...newReview,
          review: {
            title: entry,
          },
        });
        break;

      case "body":
        setNewReview({
          ...newReview,
          review: {
            body: entry,
          },
        });
        break;

      case "user":
        setNewReview({
          ...newReview,
          user: entry,
        });
        break;

      default:
        console.log("Review error");
        break;
    }
    console.log(newReview);
  };

  return (
    <div>
      <form onSubmit={handleSubmit}>
        <label>Leave a Review</label>
        <input
          type="text"
          id="title"
          value="Title"
          onChange={handleChange}
        ></input>
        <textarea value="Review" id="body" onChange={handleChange}></textarea>
        <input
          type="text"
          id="user"
          value="Name"
          onChange={handleChange}
        ></input>
        <input type="submit"></input>
      </form>
    </div>
  );
};

export default ReviewForm;
