import axios from "axios";
import { useState } from "react";

const ReviewForm = (props) => {
  const [newReview, setNewReview] = useState({});

  const handleSubmit = () => {};

  const handleChange = (e) => {
    let entry = e.target.value;

    switch (e.target.id) {
      case "title":
        setNewReview({
          ...newReview,
          review: {
            title: entry,
          },
        });
        break;

      case "body":
        setNewReview({
          ...newReview,
          review: {
            body: entry,
          },
        });
        break;

      case "user":
        setNewReview({
          ...newReview,
          user: entry,
        });
        break;
    }
  };

  return (
    <div>
      <form>
        <label>Leave a Review</label>
        <input type="text" id="title" value="Title"></input>
        <textarea value="Review" id="body"></textarea>
        <input type="text" id="user" value="Name"></input>
        <input type="submit"></input>
      </form>
    </div>
  );
};

export default ReviewForm;
