import axios from "axios";
import { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import GoogleMapReact from "google-map-react";
import "../styles/Details.css";
import Brewery from "../components/Brewery";

const Details = () => {
  let [brewery, setBrewery] = useState([]);
  let { name, lati, long } = useParams();
  let address = "Not Available";
  let longi = long.toFloat();
  let ltit = lati.toFloat();

  const defaultProps = {
    center: {
      lat: lati,
      lng: longi,
    },
    // [lati, long],
    zoom: 11,
  };

  useEffect(() => {
    const getBrewery = async () => {
      let select = await axios.get(
        `https://api.openbrewerydb.org/breweries?by_name=${name}`
      );
      setBrewery(select.data[0]);
    };
    getBrewery();
    if (brewery.street != null) {
      address =
        brewery.street +
        " " +
        brewery.city +
        ", " +
        brewery.state +
        " " +
        brewery.postal_code;
    }
  }, []);

  console.log(defaultProps.center);

  return lati === "null" ? (
    <div className={`main-bg-color details-container`}>
      <h1>{brewery.name}</h1>
      <h3>{address}</h3>
      <h3>{brewery.phone}</h3>
      <h3>{brewery.website_url}</h3>
    </div>
  ) : (
    <div className={`main-bg-color details-container`}>
      <div>
        <h1>{brewery.name}</h1>
        <h3>{address}</h3>
        <h3>{brewery.phone}</h3>
        <h3>{brewery.website_url}</h3>
        <div className={`map-container`}>
          <GoogleMapReact
            bootstrapURLKeys={{
              key: "AIzaSyCB7MuJ657ZrfijX4c3T5myVHDWOOErHb8",
            }}
            defaultCenter={defaultProps.center}
            defaultZoom={defaultProps.zoom}
          >
            {/* <Brewery
              lat={defaultProps.center[0]}
              lng={defaultProps.center[1]}
              text={`A`}
            /> */}
          </GoogleMapReact>
        </div>
      </div>
    </div>
  );
};

export default Details;
