import axios from "axios";
import { useState, useEffect } from "react";
import Button from "@mui/material/Button";
import TextField from "@mui/material/TextField";
import Dialog from "@mui/material/Dialog";
import DialogActions from "@mui/material/DialogActions";
import DialogContent from "@mui/material/DialogContent";

import ReviewForm from "./ReviewForm";

const Reviews = ({ brewery }) => {
  const [toggle, setToggle] = useState(false);
  const [open, setOpen] = useState(false);
  let [reviews, setReviews] = useState([]);

  useEffect(() => {
    const getReviews = async () => {
      let reviewList = await axios.get(
        `http://localhost:3001/review/${brewery.id}`
      );
      setReviews(reviewList.data);
    };
    getReviews();
  }, [toggle]);

  const handleDelete = (e) => {};

  const handleOpen = () => {
    setToggle(true);
  };

  const handleEdit = (e) => {
    setOpen(true);
  };

  const handleClose = () => {
    setOpen(false);
  };

  return toggle ? (
    <div className={`main-bg-color`}>
      <ReviewForm brewery={brewery} />
      {reviews.map((review) => (
        <div>
          <h2>{review.title}</h2>
          <h3>{review.user}</h3>
          <p>{review.body}</p>
          <button name={`${review._id}`}>Edit</button>
          <button name={`${review._id}`}>Delete</button>
          <Dialog open={open}>
            <DialogContent>
              <TextField
                autoFocus
                margin="dense"
                id="title"
                label="Title"
                type="text"
                fullWidth
                variant="standard"
                value={`${review.title}`}
              />
              <TextField
                autoFocus
                margin="dense"
                id="user"
                label="Name"
                type="text"
                fullWidth
                variant="standard"
                value={`${review.user}`}
              />
              <TextField
                autoFocus
                margin="dense"
                id="body"
                label="Review"
                type="text"
                fullWidth
                variant="standard"
                value={`${review.body}`}
              />
            </DialogContent>
            <DialogActions>
              <Button onClick={handleClose}>Cancel</Button>
              <Button>Submit</Button>
            </DialogActions>
          </Dialog>
        </div>
      ))}
    </div>
  ) : (
    <div>
      <Button variant="standard" onClick={handleOpen}>
        Open Reviews
      </Button>
    </div>
  );
};

export default Reviews;
